import React, { useEffect, useState } from "react";
import axios from "axios";

const App = () => {
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get("https://60ac9dff9e2d6b0017457815.mockapi.io/ag/contacts")
      .then((response) => {
        console.log(response.data); // Log full API response
        setContacts(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching contacts:", error);
        setLoading(false);
      });
  }, []);


  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600 p-6 flex flex-col items-center text-white">
      <h1 className="text-4xl font-bold mb-6">ðŸ“ž Contact List</h1>
      {loading ? (
        <p className="text-lg animate-pulse">Loading contacts...</p>
      ) : (
        <ul className="w-full max-w-md bg-white shadow-lg rounded-lg p-4 divide-y divide-gray-200">
        {contacts.map((contact) => (
          <li
            key={contact.id}
            className="flex items-center gap-4 p-3 hover:bg-gray-100 transition rounded-lg"
          >
            {/* Profile Picture */}
            <img
              src={contact.avatar || "https://via.placeholder.com/50"}
              alt={contact.name}
              className="w-14 h-14 rounded-full border-2 border-blue-500 shadow-sm"
              onError={(e) => (e.target.src = "https://via.placeholder.com/50")}
            />

            {/* Contact Details */}
            <div>
              <p className="text-lg font-semibold text-gray-900">{contact.name}</p>
              <p className="text-gray-500">{contact.email}</p>
              <p className="text-gray-600">{contact.phone || "No phone available"}</p>
              <p className="text-gray-700 font-medium">{contact.company || "No company info"}</p>
            </div>
          </li>
        ))}
      </ul>
      )}
    </div>
  );
};

export default App;
