import React, { useEffect, useState } from "react";
import axios from "axios";

const App = () => {
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);

  // useEffect(() => {
  //   axios
  //     .get("https://60ac9dff9e2d6b0017457815.mockapi.io/ag/contacts")
  //     .then((response) => {
